/* Import Google Fonts */
@import url('https://fonts.googleapis.com/css2?family=Poppins:wght@300;400;500;600;700&display=swap');

/* CSS Variables */
:root {
    --primary: #3b82f6;
    --secondary: #10b981;
    --dark: #1e293b;
    --light: #f8fafc;
}

/* Base Styles */
body {
    font-family: 'Poppins', sans-serif;
    background-color: #f1f5f9;
    color: var(--dark);
    overflow-x: hidden;
    transition: background-color 0.3s, color 0.3s;
}

/* Dark Mode Styles */
.dark {
    background-color: #0f172a;
    color: #f8fafc;
}

.dark .card {
    background-color: #1e293b;
    color: #f8fafc;
}

.dark .bg-white {
    background-color: #1e293b !important;
    color: #f8fafc;
}

.dark .bg-slate-50 {
    background-color: #0f172a !important;
}

.dark .text-slate-600, .dark .text-slate-700 {
    color: #e2e8f0 !important;
}

.dark .bg-gradient-to-br {
    background-image: linear-gradient(to bottom right, #1e293b, #1e293b) !important;
}

.dark .border-slate-300 {
    border-color: #334155 !important;
}

.dark .shadow-md {
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.2), 0 2px 4px -1px rgba(0, 0, 0, 0.12) !important;
}

/* Gradient Text */
.gradient-text {
    background: linear-gradient(90deg, var(--primary), var(--secondary));
    -webkit-background-clip: text;
    background-clip: text;
    color: transparent;
}

/* Card Styles */
.card {
    transition: all 0.3s ease;
    transform: translateY(0);
}

.card:hover {
    transform: translateY(-10px);
    box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
}

/* Timeline Styles */
.timeline-item::before {
    content: '';
    position: absolute;
    left: -40px;
    top: 0;
    width: 20px;
    height: 20px;
    border-radius: 50%;
    background: var(--primary);
    border: 4px solid white;
}

.dark .timeline-item::before {
    border-color: #1e293b;
}

.timeline::before {
    content: '';
    position: absolute;
    left: 30px;
    top: 0;
    height: 100%;
    width: 2px;
    background: #e2e8f0;
}

.dark .timeline::before {
    background: #334155;
}

/* Skill Bar Styles */
.skill-bar {
    height: 8px;
    border-radius: 4px;
    background: #e2e8f0;
    overflow: hidden;
}

.dark .skill-bar {
    background: #334155;
}

.skill-progress {
    height: 100%;
    background: linear-gradient(90deg, var(--primary), var(--secondary));
    transition: width 1s ease-in-out;
}

/* Skill progress bar styles */
.skill-progress-95 { width: 95% !important; }
.skill-progress-90 { width: 90% !important; }
.skill-progress-88 { width: 88% !important; }
.skill-progress-85 { width: 85% !important; }

/* Animations */
.floating {
    animation: floating 3s ease-in-out infinite;
}

@keyframes floating {
    0% { transform: translateY(0px); }
    50% { transform: translateY(-15px); }
    100% { transform: translateY(0px); }
}

.fade-in {
    opacity: 0;
    animation: fadeIn 1s ease-in forwards;
}

@keyframes fadeIn {
    to { opacity: 1; }
}

.delay-1 { animation-delay: 0.2s; }
.delay-2 { animation-delay: 0.4s; }
.delay-3 { animation-delay: 0.6s; }
.delay-4 { animation-delay: 0.8s; }

.glow {
    box-shadow: 0 0 15px rgba(59, 130, 246, 0.5);
}

/* Typewriter Effect */
.typewriter {
    overflow: hidden;
    border-right: 3px solid var(--primary);
    white-space: nowrap;
    animation: typing 3.5s steps(40, end), blink-caret 0.75s step-end infinite;
}

@keyframes typing {
    from { width: 0 }
    to { width: 100% }
}

@keyframes blink-caret {
    from, to { border-color: transparent }
    50% { border-color: var(--primary) }
}

/* Parallax */
.parallax {
    background-attachment: fixed;
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
}

/* Hover Underline Animation */
.hover-underline-animation {
    display: inline-block;
    position: relative;
}

.hover-underline-animation::after {
    content: '';
    position: absolute;
    width: 100%;
    transform: scaleX(0);
    height: 2px;
    bottom: 0;
    left: 0;
    background: linear-gradient(90deg, var(--primary), var(--secondary));
    transform-origin: bottom right;
    transition: transform 0.25s ease-out;
}

.hover-underline-animation:hover::after {
    transform: scaleX(1);
    transform-origin: bottom left;
}

/* Dark Mode Toggle */
.toggle-checkbox:checked {
    right: 0;
    background-color: #10b981;
}

.toggle-checkbox:checked + .toggle-label {
    background-color: #1e293b;
}

/* 3D Character Container */
.character-container {
    width: 300px;
    height: 300px;
    position: relative;
    margin: 0 auto;
}

@media (max-width: 768px) {
    .character-container {
        width: 250px;
        height: 250px;
    }
}

/* Floating Animations */
@keyframes float1 {
    0% { transform: translateY(0px); }
    50% { transform: translateY(-20px); }
    100% { transform: translateY(0px); }
}

@keyframes float2 {
    0% { transform: translateY(0px) rotate(0deg); }
    50% { transform: translateY(-10px) rotate(5deg); }
    100% { transform: translateY(0px) rotate(0deg); }
}

.float-animation1 {
    animation: float1 4s ease-in-out infinite;
}

.float-animation2 {
    animation: float2 5s ease-in-out infinite;
}

/* Cursor Interaction Styles */
.character-hover-effect {
    transition: transform 0.3s ease;
}

/* Terminal Styles */
.terminal-output {
    height: 300px;
    overflow-y: auto;
}

.blinking-cursor {
    animation: blink 1s infinite;
}

@keyframes blink {
    0%, 100% { opacity: 1; }
    50% { opacity: 0; }
}

.command:before {
    content: "$ ";
    color: #4F46E5;
    font-weight: bold;
}

.prompt:before {
    content: "visitor@portfolio:~$ ";
    color: #10B981;
    font-weight: bold;
}

/* Additional responsive styles for compatibility */
.scrollbar-thin {
    scrollbar-width: thin;
    scrollbar-color: #4F46E5 #1E293B;
}

.scrollbar-thin::-webkit-scrollbar {
    width: 8px;
}

.scrollbar-thin::-webkit-scrollbar-track {
    background: #1E293B;
}

.scrollbar-thin::-webkit-scrollbar-thumb {
    background-color: #4F46E5;
    border-radius: 4px;
}

/* Roadmap Styles */
.timeline-dot {
    width: 12px;
    height: 12px;
    background-color: #3b82f6;
    border-radius: 50%;
    position: absolute;
    left: 10px;
    top: 50%;
    transform: translateY(-50%);
}

/* About Section Styles */
.hover-scale:hover {
    transform: scale(1.05);
}

/* Responsive Adjustments */
@media (max-width: 1024px) {
    .hero-content {
        flex-direction: column;
        text-align: center;
    }
    
    .hero-buttons {
        justify-content: center;
    }
    
    #about .grid-cols-3 {
        grid-template-columns: 1fr;
    }
    
    .timeline::before {
        left: 20px;
    }
    
    .timeline-item::before {
        left: -30px;
    }
}

@media (max-width: 768px) {
    .project-grid {
        grid-template-columns: 1fr;
    }
    
    .skill-grid {
        grid-template-columns: 1fr;
    }
    
    .contact-box {
        flex-direction: column;
    }
    
    .contact-info {
        width: 100%;
    }
    
    .contact-form {
        width: 100%;
    }
    
    .footer-content {
        flex-direction: column;
        text-align: center;
    }
    
    .footer-social {
        justify-content: center;
        margin-top: 1rem;
    }
    
    /* Adjust roadmap for mobile */
    .roadmap-item-right, .roadmap-item-left {
        margin-left: 20px;
    }
    
    .roadmap-dot {
        left: -16px;
    }
    
    .roadmap-connector {
        left: -8px;
    }
}

/* Cross-browser user-select fix */
.no-select {
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
}

/* No select for particles */
.particles, .particles * {
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
}

/* Preloader */
.preloader {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: #0f172a;
    display: flex;
    justify-content: center;
    align-items: center;
    z-index: 9999;
    transition: opacity 0.5s ease;
}

.preloader-content {
    text-align: center;
}

.spinner {
    width: 70px;
    height: 70px;
    border: 5px solid rgba(255, 255, 255, 0.3);
    border-radius: 50%;
    border-top-color: #3b82f6;
    animation: spin 1s ease-in-out infinite;
    margin: 0 auto 1rem;
}

@keyframes spin {
    to { transform: rotate(360deg); }
}
